  List<Game> selectedGames = [];
  int rounds = 1;
  Map<String, int> gamePlayCounts = {};
  List<Map<String, dynamic>> recentGames = [];

  GameProvider() {
    _loadGameState();
  }

  Future<void> _loadGameState() async {
    final loadedGames = await StorageService.loadSelectedGames();
    if (loadedGames.isNotEmpty) {
      selectedGames = loadedGames.map((name) {
        return defaultGames.firstWhere(
              (game) => game.name == name,
          orElse: () => Game(
            name: name,
            explanation: "",
            imageUrl: "",
            difficulty: "Medium",
            minPlayers: 2,
            maxPlayers: 4,
          ),
        );
      }).toList();
    } else {
      selectedGames = List.from(defaultGames);
    }
    rounds = await StorageService.loadRounds();
    notifyListeners();
  }

  Future<void> saveGameState() async {
    await StorageService.saveSelectedGames(selectedGames.map((game) => game.name).toList());
    await StorageService.saveRounds(rounds);
  }

  List<Game> getGames() {
    return List.from(defaultGames);
  }

  void resetSelectedGames() {
    selectedGames = List.from(defaultGames);
    saveGameState();
    notifyListeners();
  }

  void setSelectedGames(List<Game> games) {
    selectedGames = games;
    saveGameState();
    notifyListeners();
  }

  void setRounds(int roundsCount) {
    rounds = roundsCount;
    saveGameState();
    notifyListeners();
  }

  void recordGamePlayed(String gameName) {
    gamePlayCounts[gameName] = (gamePlayCounts[gameName] ?? 0) + 1;
    notifyListeners();
  }

  List<MapEntry<String, int>> getMostPlayedGames() {
    final sortedGames = gamePlayCounts.entries.toList()
      ..sort((a, b) => b.value.compareTo(a.value));
    return sortedGames;
  }

  void addRecentGame(String gameName, Map<String, int> playerScores) {
    if (recentGames.length >= 5) {
      recentGames.removeAt(0);
    }
    recentGames.add({
      'game': gameName,
      'scores': playerScores,
      'timestamp': DateTime.now(),
    });
    saveGameState();
    notifyListeners();
  }
